import { useEffect, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import ManagerService from '../services/Manager.service';
import employeeService from '../services/employee.service';
import ReadOnlyRow from "../components/ReadOnlyRow";
import EditableRow from "../components/EditableRow";


const ManagerList = () => {
    const [manager, setManager] = useState([]);
    const [addFormData, setAddFormData] = useState({
    mgrname: "",
    id: "",
    name: "",
    gender: "",
    dept:"",

  });
  const handleAddFormChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("mgrname");
    const fieldValue = event.target.value;

    const newFormData = { ...addFormData };
    newFormData[fieldName] = fieldValue;

    setAddFormData(newFormData);
  };
    const init = () => {
        ManagerService.getAll()
          .then(response => {
            console.log('Printing manager data', response.data);
            setManager(response.data);
          })
          .catch(error => {
            console.log('Something went wrong', error);
          }) 
      }
    
      useEffect(() => {
        init();
      }, []);


  return (
    <div className="container">
  
      <h3>Managers List</h3>
      <hr/>
      
      <div>
        
        <table className="table table-bordered table-striped">
          <thead className="thead-dark">
            <tr>
              
              <th>Manager Name</th>
              <th>Employee ID</th>
              <th>Employee Name</th>
              <th>Manager Gender</th>
              <th>Manager Department</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
          {
              
            manager.map(mgr => (
              <tr key={mgr.mgrid}>
                <td>{mgr.mgrname}</td>
                <td>{mgr.id}</td>
                <td>{mgr.name}</td>
                <td>{mgr.gender}</td>
                <td>{mgr.dept}</td>
                <td>
                  <Link className="btn btn-info" to={`/manager/edit/${mgr.mgrid}`}>Update</Link>
                  
                  <button className="btn btn-danger ml-2" onClick={() => {
                    if(window.confirm('Are you sure?')){
                     // handleDelete(mgr.mgrid);
                    }
                    
                  }}>Delete</button>
                </td>
              </tr>
            ))
            
          }
          
          </tbody>
        </table>
        
      </div>
      <h2>Add Manager</h2>
      <form>
      <input 
            type="text" 
            required="required"
            name="mgrname"
            id="mgrname"
           onChange={handleAddFormChange}
            placeholder="Manager Name"
            ></input>
         &nbsp; &nbsp;
        <input
          type="text"
          required="required"
         
          id="id"
          placeholder="Enter Employee ID."
          name="id"
         
         onChange={handleAddFormChange}
        
        >
        </input>
        &nbsp; &nbsp;
        <input
          type="text"
          required="required"
         
          placeholder="Enter Employee Name"
          name="name"
          id="name"
         
         onChange={handleAddFormChange}
        ></input>
        &nbsp; &nbsp;
        <input
          type="text"
          required="required"
          placeholder="Enter the Gender"
          name="gender"
          id="gender"
          
         onChange={handleAddFormChange}
        ></input>
        &nbsp; &nbsp;
        <input
          type="text"
          required="required"
          placeholder="Enter the Department"
          name="department"
          id="dept"
          
         onChange={handleAddFormChange}
        ></input>
        &nbsp; &nbsp;
        <button type="submit" className="btn btn-primary mb-2">Add</button>
      </form>
    </div>
  );
}

export default ManagerList;
